package problem5;
/**
 * This program demonstrates For-loop working 
 */

public class For_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i;
		System.out.print("The number are .......");
		System.out.println();
		for(i=0;i<10;i++) {
			System.out.println(i);
		}

	}
}
